import { TPermissions } from 'types/dist/common.types';

declare const useLocalPeer: <T = unknown>(props?: {
    onMetadataUpdated?: ((metadata: T) => void) | undefined;
    onPermissionsUpdated?: ((permissions: TPermissions) => void) | undefined;
    onRoleUpdated?: ((role: string) => void) | undefined;
} | undefined) => {
    peerId: string | null;
    role: string | null;
    permissions: {
        admin: boolean;
        canConsume: boolean;
        canProduce: boolean;
        canProduceSources: {
            cam: boolean;
            mic: boolean;
            screen: boolean;
        };
        canSendData: boolean;
        canRecvData: boolean;
        canUpdateMetadata: boolean;
    };
    metadata: T | null;
    updateMetadata: (data: T) => void;
    updateRole: (data: {
        role: string;
    }) => void;
};

export { useLocalPeer };
